import React, { Component } from 'react'
import ProjectService from '../services/ProjectService'

class ListCourseComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
                students: []
        }
        this.addCourse = this.addCourse.bind(this);
        this.editCourse = this.editCourse.bind(this);
        this.deleteCourse = this.deleteCourse.bind(this);
    }

    deleteCourse(id){
        ProjectService.deleteStudent(id).then( res => {
            this.setState({students: this.state.students.filter(student => student.id !== id)});
        });
    }
    viewCourse(id){
        this.props.history.push(`/view-course/${id}`);
    }
    editCourse(id){
        this.props.history.push(`/add-course/${id}`);
    }

    componentDidMount(){
        ProjectService.getStudents().then((res) => {
            this.setState({ students: res.data});
        });
    }

    addCourse(){
        this.props.history.push('/add-course/_add');
    }

    render() {
        return (
            <div>
                
                 <div className = "row main_content">
                    <button className="btn btn-primary" onClick={this.addCourse}> Add Course</button>
                 </div>
                 <br></br>
                 <div className = "row">
                        <table className = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th> Course Name</th>
                                    <th> Student Last Name</th>
                                    <th> Courses</th>
                                    <th> Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.students.map(
                                        student => 
                                        <tr key = {student.id}>
                                             <td> { student.firstName} </td>   
                                             <td> {student.lastName}</td>
                                             <td> {student.emailId}</td>
                                             <td>
                                                 <button onClick={ () => this.editStudent(student.id)} className="btn btn-info">Update </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.deleteStudent(student.id)} className="btn btn-danger">Delete </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.viewStudent(student.id)} className="btn btn-info">View </button>
                                             </td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>

                 </div>

            </div>
        )
    }
}

export default ListCourseComponent
