import React, { Component } from 'react'
import ProjectService from '../services/ProjectService';
import { InputGroup,FormControl,Button } from 'react-bootstrap';

class EditCourseComponent extends Component {
    constructor(props) {
        super(props)

    
        this.state = {
            students: [],
            // step 2
            id: this.props.match.params.id,
            name: '',
            studentId: '',
        }
        this.changeNameHandler = this.changeNameHandler.bind(this);
        this.changeAddSelectorHandler = this.changeAddSelectorHandler.bind(this);
        this.saveOrUpdateEmployee = this.saveOrUpdateEmployee.bind(this);
    }

    // step 3
    componentDidMount(){

        ProjectService.getStudents().then( (res) =>{
            this.setState({ students: res.data});
        });

        // step 4
        if(this.state.id === '_add'){
            return
        }else{
            ProjectService.getCourseById(this.state.id).then( (res) =>{
                let course = res.data;
                this.setState(
                    {
                        name: course.name
                    }
                );
            });
        }        
    }
    saveOrUpdateEmployee = (e) => {
        e.preventDefault();
        let employee = {name: this.state.name, studentId: this.state.studentId};
        console.log('employee => ' + JSON.stringify(employee));

        // step 5
        // if(this.state.id === '_add'){
        //     ProjectService.createEmployee(employee).then(res =>{
        //         this.props.history.push('/employees');
        //     });
        // }else{
        //     ProjectService.updateEmployee(employee, this.state.id).then( res => {
        //         this.props.history.push('/employees');
        //     });
        // }
    }
    
    changeNameHandler= (event) => {
        this.setState({name: event.target.value});
    }

    changeAddSelectorHandler= (event) => {
        this.setState({studentId: event.target.value});
    }


    cancel(){
        this.props.history.push('/courses');
    }

    getTitle(){
        if(this.state.id === '_add'){
            return <h3 className="text-center">Add Course</h3>
        }else{
            return <h3 className="text-center">Update Course</h3>
        }
    }



    render() {
        return (
            <div>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                <div className = "card-title">
                                    {
                                        this.getTitle()
                                    }
                                </div>
                                <div className = "card-body">
                                    <form>
                                        <div className = "form-group">
                                            <label> Subject Name: </label>
                                            <InputGroup className="mb-3">
                                                <FormControl
                                                placeholder="Subject Name"
                                                aria-label="Subject Name"
                                                value={this.state.name} onChange={this.changeNameHandler}
                                                aria-describedby="basic-addon2"
                                                />
                                                <InputGroup.Append>
                                                    <Button variant="outline-secondary">Update</Button>
                                                </InputGroup.Append>
                                            </InputGroup>
                                        </div>
                                        
                                        <div className = "form-group">
                                            
                                            <label> Add Student: </label>

                                            <InputGroup className="mb-3">
                                                <select className="form-control" onChange={this.changeAddSelectorHandler} value={this.state.studentId}>
                                                    <option selected>Select Student</option>
                                                    <option value="1">Maruf</option>
                                                    <option value="2">Reza</option>
                                                    {
                                                        this.state.students.map(
                                                            student =>
                                                            <option key={student} value={student.id}>
                                                                {student.name}
                                                            </option>
                                                        )
                                                    }
                                                </select>
                                                <InputGroup.Append>
                                                    <Button variant="outline-secondary"><pre>Add</pre></Button>
                                                </InputGroup.Append>
                                            </InputGroup>
                                        </div>

                                        <div className = "form-group">
                                            
                                            <label> Remove Student: </label>

                                            <InputGroup className="mb-3">
                                                <select className="form-control" onChange={this.changeAddSelectorHandler} value={this.state.studentId}>
                                                    <option selected>Select Student</option>
                                                    <option value="1">Maruf</option>
                                                    <option value="2">Reza</option>
                                                    {
                                                        this.state.students.map(
                                                            student =>
                                                            <option key={student} value={student.id}>
                                                                {student.name}
                                                            </option>
                                                        )
                                                    }
                                                </select>
                                                <InputGroup.Append>
                                                    <Button variant="outline-secondary">Remove</Button>
                                                </InputGroup.Append>
                                            </InputGroup>
                                        </div>

                                        {/* <button className="btn btn-success" onClick={this.saveOrUpdateEmployee}>Save</button> */}
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default EditCourseComponent
