import React, { Component } from 'react'
import ProjectService from '../services/ProjectService'

class ViewCourseComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
                courses: []
        }
        this.addCourse = this.addCourse.bind(this);
        this.editCourse = this.editCourse.bind(this);
        this.deleteCourse = this.deleteCourse.bind(this);
    }

    deleteCourse(id){
        ProjectService.deleteStudent(id).then( res => {
            this.setState({courses: this.state.courses.filter(course => course.id !== id)});
        });
    }
    viewCourse(id){
        this.props.history.push(`/view-course/${id}`);
    }
    editCourse(id){
        this.props.history.push(`/add-course/${id}`);
    }

    componentDidMount(){
        ProjectService.getStudents().then((res) => {
            this.setState({ courses: res.data});
        });
    }

    addCourse(){
        this.props.history.push('/add-course/_add');
    }

    getTitle(){
        if(this.state.id === '_add'){
            return <h3 className="text-center">Add Course</h3>
        }else{
            return <h3 className="text-center">Update Course</h3>
        }
    }

    render() {
        return (
            <div>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                <div className = "card-title">
                                    {
                                        this.getTitle()
                                    }
                                </div>
                                <div className = "card-body">
                                    <form>
                                        <div className = "form-group">
                                            <label> Subject Name: </label>
                                            <input placeholder="Subject Name" name="name" className="form-control" 
                                                value={this.state.firstName} onChange={this.changeFirstNameHandler}/>
                                        </div>
                                        
                                        <div className = "form-group">
                                            
                                            <label> Add Student: </label>

                                            <select className="form-control">
                                                <option selected>Select Student</option>
                                                {
                                                    this.state.courses.map(
                                                        course =>
                                                        <option key={course} value={course.id}>
                                                            {course.name}
                                                        </option>
                                                    )
                                                }
                                            </select>
                                        </div>

                                        {
                                            this.setRemoveDropdown()
                                        }

                                        <button className="btn btn-success" onClick={this.saveOrUpdateEmployee}>Save</button>
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default ViewCourseComponent
