import React, { Component } from 'react'
import ProjectService from '../services/ProjectService';


class CreateCourseComponent extends Component {
    constructor(props) {
        super(props)

    
        this.state = {
            courses: [],
            // step 2
            id: this.props.match.params.id,
            firstName: '',
            lastName: '',
            emailId: ''
        }
        this.changeFirstNameHandler = this.changeFirstNameHandler.bind(this);
        this.changeLastNameHandler = this.changeLastNameHandler.bind(this);
        this.saveOrUpdateEmployee = this.saveOrUpdateEmployee.bind(this);
    }

    // step 3
    componentDidMount(){

        ProjectService.getCourses().then( (res) =>{
            this.setState({ courses: res.data});
        });

        // step 4
        if(this.state.id === '_add'){
            return
        }else{
            ProjectService.getStudentById(this.state.id).then( (res) =>{
                let employee = res.data;
                this.setState({firstName: employee.firstName,
                    lastName: employee.lastName,
                    emailId : employee.emailId
                });
            });
        }        
    }
    saveOrUpdateEmployee = (e) => {
        e.preventDefault();
        let employee = {firstName: this.state.firstName, lastName: this.state.lastName, emailId: this.state.emailId};
        console.log('employee => ' + JSON.stringify(employee));

        // step 5
        if(this.state.id === '_add'){
            ProjectService.createEmployee(employee).then(res =>{
                this.props.history.push('/employees');
            });
        }else{
            ProjectService.updateEmployee(employee, this.state.id).then( res => {
                this.props.history.push('/employees');
            });
        }
    }
    
    changeFirstNameHandler= (event) => {
        this.setState({firstName: event.target.value});
    }

    changeLastNameHandler= (event) => {
        this.setState({lastName: event.target.value});
    }

    changeEmailHandler= (event) => {
        this.setState({emailId: event.target.value});
    }

    cancel(){
        this.props.history.push('/courses');
    }

    getTitle(){
        if(this.state.id === '_add'){
            return <h3 className="text-center">Add Course</h3>
        }else{
            return <h3 className="text-center">Update Course</h3>
        }
    }

    setRemoveDropdown(){
        if(this.state.id === '_add'){
            return null
        }else{
            return <div className = "form-group">
                                            
            <label> Remove Student: </label>

                <select className="form-control">
                    <option selected>Select Student</option>
                    {
                        this.state.courses.map(
                            course =>
                            <option key={course} value={course.id}>
                                {course.name}
                            </option>
                        )
                    }
                </select>
            </div>
        }
    }

    render() {
        return (
            <div>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                <div className = "card-title">
                                    {
                                        this.getTitle()
                                    }
                                </div>
                                <div className = "card-body">
                                    <form>
                                        <div className = "form-group">
                                            <label> Course Name: </label>
                                            <input placeholder="First Name" name="firstName" className="form-control" 
                                                value={this.state.firstName} onChange={this.changeFirstNameHandler}/>
                                        </div>
                                        
                                        <div className = "form-group">
                                            
                                            <label> Add Student: </label>

                                            <select className="form-control">
                                                <option selected>Select Student</option>
                                                {
                                                    this.state.courses.map(
                                                        course =>
                                                        <option key={course} value={course.id}>
                                                            {course.name}
                                                        </option>
                                                    )
                                                }
                                            </select>
                                        </div>

                                        {
                                            this.setRemoveDropdown()
                                        }

                                        <button className="btn btn-success" onClick={this.saveOrUpdateEmployee}>Save</button>
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default CreateCourseComponent
